import { NowRequest, NowResponse } from '@now/node'
import StockUser from '../../../models/stockuser.model'

export default async (req: NowRequest, res: NowResponse) => {
  try {
    var number = req.body.number;
    console.log(number)
    const user = (await StockUser.find({"_id": number}));
    console.log(user);
    console.log(req.body.code)
    console.log(user.code == req.body.code)
    if ((user.code == req.body.code)){
        await StockUser.updateOne({_id: number}, {$set: { signed: true }}, {upsert : true, setDefaultsOnInsert: true});
        res.status(200).send("success");
    }
    else res.status(400).send("failed");
  } catch (error) {
    res.status(502).send(error);
  }
  
}
